mapper.select:
SELECT d.*, dt.device_type_code, dt.device_type_name, p.proxy_code, p.proxy_name, p.proxy_address, p.proxy_mobile, p.proxy_operater, ip.* FROM device d INNER JOIN device_type dt ON d.TYPE_CODE = dt.device_type_code LEFT JOIN proxy p ON p.proxy_code = d.PROXY_CODE LEFT JOIN installplace ip ON d.INSYALL_PLEACE_CODE = ip.INSTALL_PLACE_CODE
AND d.is_delete != 1
AND d.DEVICE_STATE =#{state}
AND d.TYPE_CODE =#{type}
AND (d.DEVICE_CODE LIKE CONCAT("%",#{key},"%") OR ip.INSTALL_PLACE_NAME LIKE CONCAT("%",#{key},"%"))
AND ip.INSTALL_PLACE_CREATE_TIME >= #{startTime}
AND ip.INSTALL_PLACE_CREATE_TIME <= #{endTime}
ORDER BY d.UPDATE_TIME DESC
