mapper.select:
SELECT * FROM warming

    AND device_code like CONCAT('%',#{deviceCode},'%')

        AND ishandle = #{query.ishandle}


        AND warming_time >= #{query.startTime}


        AND warming_time <= #{query.endTime}


ORDER BY device_code ,warming_time DESC

SELECT d.DEVICE_CODE as device_code, w.*, t.device_type_name as type_name FROM warming w INNER JOIN device d ON w.device_code = d.DEVICE_CODE INNER JOIN device_type t on d.TYPE_CODE = t.device_type_code
AND w.device_code IN
#{code}
AND w.ishandle = #{query.ishandle}
AND w.warming_time >= #{query.startTime}
AND w.warming_time <= #{query.endTime}
ORDER BY w.device_code ,w.warming_time DESC

SELECT d.*, w.*, t.device_type_name as type_name FROM warming w INNER JOIN device d ON w.device_code = d.DEVICE_CODE INNER JOIN device_type t on d.TYPE_CODE = t.device_type_code
AND w.ishandle = #{query.ishandle}
AND w.device_code LIKE CONCAT("%",#{query.key},"%") OR w.warming_msg LIKE CONCAT("%",#{query.key},"%")
AND w.warming_time >= #{query.startTime}
AND w.warming_time <= #{query.endTime}
ORDER BY w.device_code ,w.warming_time DESC
